import React from "react";
import "../style/SignUp.css";
import { useForm } from "react-hook-form";
import { registerUser } from "../redux/thunkFunctions";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";

const SignUp = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ mode: "onChange" });
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const onSubmit = async ({ id, auNum, name, password}) => {
    try {
      const body = {
        id,
        auNum,
        name,
        password,
      };
      await dispatch(registerUser(body));
      navigate("/login");
    } catch (err) {
      console.error(err);
    }
  };

  const userId = {
    required: "필수 필드입니다.",
  };
  const userAuNum = {
    required: "필수 필드입니다.",
  }
  const userName = {
    required: "필수 필드입니다.",
  }
  const userPassword = {
    required: "필수 필드입니다.",
    minLength: {
      value: 6,
      message: "최소 6자입니다.",
    },
  };

  return (
    <div className="SignUpPage">
      <div className="subdiv">
        <div className="title">Sign Up</div>
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="contentTitle">
                <div className="inputTitle">EMAIL</div>
                <div className="inputWirte">
                  <input
                    type="text"
                    className="input"
                    {...register("id", userId)}
                  />
                  {errors?.id && (
                    <div>
                      <span>{errors.id.message}</span>
                    </div>
                  )}
                </div>
              </div>

          <div>
            <label htmlFor="auNum">인증번호</label>
            <input
              type="auNum"
              id="auNum"
              name="auNum"
              {...register("auNum", userAuNum)}
            />
            {errors?.auNum && (
              <div>
                <span>{errors.auNum.message}</span>
              </div>
            )}
          </div>

          <div>
            <label htmlFor="name">NAME</label>
            <input
              type="name"
              id="name"
              name="name"
              {...register("name", userName)}
            />
            {errors?.name && (
              <div>
                <span>{errors.name.message}</span>
              </div>
            )}
          </div>

          <div>
            <label htmlFor="password">PASSWORD</label>
            <input
              type="password"
              id="password"
              name="password"
              {...register("password", userPassword)}
            />
            {errors?.password && (
              <div>
                <span>{errors.password.message}</span>
              </div>
            )}
          </div>

          <button type="submit">회원가입</button>
        </form>
      </div>
    </div>
  );
};

export default SignUp;
